/* eslint-disable react/jsx-props-no-spreading */
import "../styles.css";
import type { AppProps } from "next/app";
import { useEffect, useState } from "react";
import Layout from "./layout";
import AppContext from "../lib/AppContext";
import useLogin from "../lib/hooks/useLogin";

interface ExtendedAppProps extends AppProps {
  userData: {
    username: string;
  };
}

function App({ Component, pageProps }: ExtendedAppProps) {
  const appDataHooks = useState({ userData: {} });
  const [appData, setAppData] = appDataHooks;
  const { userData } = useLogin(
    "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..iAjoOW4dDfYU2hd2fhnPU0aX8f8AkTKuS0wtrWrQgNI"
  );

  useEffect(() => {
    if (userData) {
      setAppData({ ...appData, userData });
    }
  }, [userData]);

  return (
    <AppContext.Provider value={appDataHooks}>
      <Layout>
        <Component {...pageProps} />
      </Layout>
    </AppContext.Provider>
  );
}

export default App;
